    private void CalcDiscActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_CalcDiscActionPerformed
int BA,Disc=0,DiscAmt,a;
BA=Integer.parseInt(BillAmtTF.getText());
if(BA>25000)
{Disc+=0.05;}
if(jCheckBox1.isSelected())
Disc+=0.05;
a=jComboBox1.getSelectedIndex();
switch(a){
    case 0:Disc+=0.02;
    break;
    case 1:Disc+=0.05;
    break;
    case 3:Disc+=0.08;
    break;
}
DiscAmt=BA*Disc;
DiscTF.setText(""+DiscAmt);
btnCalcNetAmt.setEnabled(true);//Displaying Discount Amount in TextField


// TODO add your handling code here:
    }//GEN-LAST:event_CalcDiscActionPerformed

