    private void btnCalcNetAmtActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalcNetAmtActionPerformed
int GSTamt,BA,D,NA=0;//BA-Bill amount,D-Discount,GSTamt-AMount after adding GST,NA-Net Amount
BA=Integer.parseInt(BillAmtTF.getText());
D=Integer.parseInt(DiscTF.getText());
NA=BA-D;
GSTamt=(int) (NA+(0.12*NA));
NetAmtTF.setText(""+GSTamt);
// TODO add your handling code here:
    }//GEN-LAST:event_btnCalcNetAmtActionPerformed

    private void BillAmtTFActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BillAmtTFActionPerformed
String name;
name=NameTF.getText();
int BA,DS,RC=0;//For number of Days Stayed and Room Category
DS=Integer.parseInt(DSTF.getText());//To obtain value of DS
if (jRadioButton1.isSelected())
{RC=5000;}
if (jRadioButton2.isSelected())
{RC=6500;}
if (jRadioButton3.isSelected())
{RC=8000;}
BA=RC*DS;//To obtain bill amount
BillAmtTF.setText(""+BA);        // TODO add your handling code here:
    }//GEN-LAST:event_BillAmtTFActionPerformed

